
public class Searching_Character {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			String s="Deepanjali";
			
			System.out.println(s.indexOf("p"));	//3
			System.out.println(s.lastIndexOf('e'));	//2
			System.out.println(s.charAt(5));	//n
			System.out.println(s.contains("s"));	//false
			System.out.println(s.startsWith("ee"));	//false
			System.out.println(s.endsWith("li"));	// true
	}

}
